catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	var dStartDate = g_form.getValue("start_date");
	var dEndDate = g_form.getValue("end_date");

	var ga2 = new GlideAjax('HVSExpenseDateUtils');
	ga2.addParam('sysparm_name', 'ValidateDates');
	ga2.addParam('sysparm_start',dStartDate);
	ga2.addParam('sysparm_end',dEndDate);
	ga2.getXML(CheckExpenseDates);
}

function CheckExpenseDates(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");

	try {
		if(answer == 'false') {
			getMessage('x_hdvi2_expense.endDate.not.valid', function(msg) {
				g_form.showFieldMsg('end_date',msg,'info');
			});
			g_form.setValue('formok','false');
		}
		else g_form.setValue('formok','true');
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS Check Dates - Form Demande de Remboursement]');
	}
}
