catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }
    try {
        var field = g_form.getField("mvar_hvs_rff_expense_line_input");
        if (field != null) {
            g_form.setValue('mvar_hvs_rff_expense_line_input', JSON.stringify([]));
            g_form.setMandatory('mvar_hvs_rff_expense_line_input', true);
            field.max_rows_size = 4;
        }
    } catch (e) { //desktop
        clearRows("0e6df7f1dbc62450f5e2174b0696198a");
    }

    function clearRows(sysIdMRWS) {
        TableVariableService.removeAllRows(sysIdMRWS);
    }
}
