catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below

    var $ = this.jQuery;
    var sTrainintType = g_form.getValue("expense_line");

    setTimeout(function() {
        if (sTrainintType == 'travel_expenses') {
            $("label[for='sp_formfield_travel_mode']").closest("div").css("display", "block");
			g_form.setMandatory("travel_mode",true);
        } else {
            $("label[for='sp_formfield_travel_mode']").closest("div").css("display", "none");
			g_form.setMandatory("travel_mode",false);
        }
    }, 0);

    var ga2 = new GlideAjax('HVSExpenseDateUtils');
    ga2.addParam('sysparm_name', 'GetDate');
    ga2.getXML(returnDate);

}

function returnDate(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    if (answer) {
        // 			//var vDate = new Date(getDateFromFormat(result.date, g_user_date_format));
        g_form.setValue('expense_date', answer);
    }
}
