catalog_script_client.script:
function onLoad() {

    var $ = this.jQuery,
        fieldName = "mvar_hvs_rff_expense_line_input"; // !!! it's the name of any variable from variable set !!!

    setTimeout(function() {
        var $fieldset = $("#" + fieldName).closest("fieldset"),
            $row = $fieldset.find("div.wrapper-md"),
            $button = $("<button style='margin-right:10px;margin-bottom:2px;' class='btn btn-icon'>" +
                "<i class='fa fa-minus fa-lg'></i></button>");

        $("label[for='sp_formfield_mvar_hvs_rff_expense_line_input']").addClass("h4") // find(">div>label")
            .before($button);

        $button.click(function() {
            $row.toggle();
            $button.find("i")
                .toggleClass("fa-minus fa-plus");

        });
    }, 0);
}
