catalog_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below
	var dToday = new Date();
	g_form.setValue('end_date',dToday);

	var ga = new GlideAjax('HVSExpenseUtils');
	ga.addParam('sysparm_name', 'getNbContract');
	ga.addParam('user_sys_id',g_form.getValue("requested_for"));
	ga.getXML(parseContractInfos);


	var ga2 = new GlideAjax('HVSExpenseDateUtils');
	ga2.addParam('sysparm_name','GetDate');
	ga2.getXML(returnFunction);

}

function returnFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
 		if (answer) {
// 			//var vDate = new Date(getDateFromFormat(result.date, g_user_date_format));
 			g_form.setValue('sr_date_expense',answer);
 		}
}

function parseContractInfos(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	answer = JSON.parse(answer);

	try {
		if(answer) {
			if (answer.nb < 2){
				g_form.setValue("contract", answer.id);
				g_form.setReadOnly("contract", true);
			}
			else
				{
				g_form.setReadOnly("contract", false);
				g_form.setMandatory("contract", true);
			}
		}
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: [var HVS Check Contract Length - Form Demande de Remboursement]');
	}
}
