catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	if (isLoading || newValue == '') {
		if(g_form.getValue("sr_loga_code") == '806' || g_form.getValue("sr_loga_code") == '79C' || g_form.getValue("sr_loga_code") == '79H') {
			g_form.setMandatory('sr_new_location_from_reason',false);
			g_form.setValue('sr_new_location_from_reason','');
			g_form.setValue('sr_location_to','');
			g_form.setMandatory('sr_location_to',false);
			g_form.setDisplay('sr_location_to',false);
			g_form.setMandatory('sr_location_other',true);
			g_form.setDisplay('sr_location_other',true);
			g_form.setValue('sr_requested_value','');
			g_form.setMandatory('sr_requested_value',true);
			g_form.setDisplay('sr_requested_value',true);
		}
		return;
	}

	var sLogaRubric = g_form.getDisplayValue('sr_rubrique_loga');
	if(g_form.getValue("locationid") != newValue) {
		var grUser= new GlideRecord('sys_user');
		grUser.addQuery('sys_id', ''+g_form.getValue('requested_for'));
		grUser.query(UserDepartment);
	}
	else {
		g_form.setMandatory("sr_new_location_from_reason",false);
		g_form.setDisplay("sr_new_location_from_reason",false);
	}


	if((g_form.getValue("sr_loga_code") == '79C' || g_form.getValue("sr_loga_code") == '79H') && g_form.getDisplayValue("sr_location_from")=='Lausanne') {
		getMessage('expense.modal.warning', function(modalTitle) {
			getMessage('expense.location.not.allowed', function(msg) {
				if (typeof spModal != 'undefined') {
					spModal.open({
						title: modalTitle,
						message: msg,
						buttons: [
							{label:'OK', primary: true}
						],
					backdrop: 'static'
					}).then(function(){
						g_form.setValue('sr_location_from',g_form.getValue("locationid"));
						return;
					});
				}			
			});
		});
	}

	if(g_form.getValue("sr_loga_code") == '806' || g_form.getValue("sr_loga_code") == '79C'  || g_form.getValue("sr_loga_code") == '79H') {
		if((g_form.getValue("sr_rubrique_loga")!='edc49704dbd3c050f5e2174b06961901' && g_form.getValue("sr_loga_code") == '79C') || (g_form.getValue("sr_rubrique_loga")!='d7a2f22bdb3fcc50011e177e1696199b' && g_form.getValue("sr_loga_code") == '79H')) {
			g_form.setMandatory('sr_location_to',true);
			g_form.setDisplay('sr_location_to',true);
		}
		g_form.setMandatory('sr_location_other',false);
		g_form.setDisplay('sr_location_other',false);

		var ga2 = new GlideAjax('HVSExpenseUtils');
		ga2.addParam('sysparm_name', 'getNbKM');
		ga2.addParam('sysparm_locfrom',g_form.getDisplayValue('sr_location_from'));
		ga2.addParam('sysparm_locto',g_form.getDisplayValue('sr_location_to'));
		ga2.getXML(CheckLocationsKM);
	}
}

function UserDepartment(grUser){
	if(grUser.next()) {
		var DepartmentId = grUser.department;
		if(DepartmentId !='119669deffa26c407102114e40b9ad6b') {
			g_form.setDisplay("sr_new_location_from_reason",true);
			g_form.setMandatory("sr_new_location_from_reason",true);
		}
		else {
			g_form.setMandatory("sr_new_location_from_reason",false);
			g_form.setDisplay("sr_new_location_from_reason",false);
		}
	}
}

function CheckLocationsKM(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	answer = JSON.parse(answer);

	try {
		if(answer && answer.value!=0) {
			g_form.setValue('sr_requested_value',answer.value);
			g_form.setReadonly('sr_requested_value',true);
		}
// 		else {
// 			g_form.setValue('sr_requested_value','');
// 			g_form.setMandatory('sr_requested_value',true);
// 		}	
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS Check Dates - Form Demande de Remboursement]');
	}
}
