catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var dDate = g_form.getValue("expense_date");

    var ga2 = new GlideAjax('HVSExpenseDateUtils');
    ga2.addParam('sysparm_name', 'ValidateExpenseLineDate');
    ga2.addParam('sysparm_date', dDate);
    ga2.getXML(CheckExpenseDates);

    var ga = new GlideAjax('HVSExpenseUtils');
    ga.addParam('sysparm_name', 'checkExpenseDate');
    ga.addParam('sysparm_TreqId', this.my_g_form.getValue("training_request"));
    ga.addParam('sysparm_ExpenseDate', dDate);
    ga.getXML(parseTrainingData);

}

function CheckExpenseDates(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var oldDate = g_form.getValue("expense_date");

    try {
        var $ = this.jQuery;
        if (answer == 'false') {
            getMessage('x_hdvi2_expense.lineDate.not.valid', function(msg) {
                g_form.showFieldMsg('expense_date', msg.replace('{0}', oldDate), 'info');
            });
            $("button[ng-click*='c.saveRow']").addClass("disabled");

        } else {
            $("button[ng-click*='c.saveRow']").removeClass("disabled");
        }
    } catch (err) {
        jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS RFF Check Date Expense - Form Demande de Remboursement Formation]');
    }
}

function parseTrainingData(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    answer = JSON.parse(answer);

    try {
		var $ = this.jQuery;
        if (!answer.bDateOk) {
            getMessage('HVS RFF - Training Period Dates', function(msg) {
                 g_form.showFieldMsg('expense_date', msg.withValues([this.my_g_form.getDisplayValue("training_request"), answer.start_date_dv, answer.end_date_dv]));
            });
            $("button[ng-click*='c.saveRow']").addClass("disabled");

        } else {
            $("button[ng-click*='c.saveRow']").removeClass("disabled");
        }
    } catch (err) {
        jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS RFF - GetDate form Training Data - Form Demande de Remboursement Formation]');
    }
}
