catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {



    var ga2 = new GlideAjax('HVSExpenseUtils');
    ga2.addParam('sysparm_name', 'getNbKM');
    ga2.addParam('sysparm_locfrom', g_form.getDisplayValue('sr_location_from'));
    ga2.addParam('sysparm_locto', g_form.getDisplayValue('sr_location_to'));
    ga2.getXML(CheckLocationsKM);
}

function CheckLocationsKM(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    answer = JSON.parse(answer);

    try {
        if (answer) {
            if (g_form.getValue("sr_one_way") == "true") {
                var km = parseInt(answer.value);
                km = km / 2;
                g_form.setValue('sr_requested_value', km);
            } else {
                g_form.setValue('sr_requested_value', answer.value);
            }

        }
    } catch (err) {
        jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS Retrieve KM when On Way changes - Form Demande de Remboursement]');
    }
}
