catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('HVSExpenseUtils');
    ga.addParam('sysparm_name', 'getUserLanguage');
    ga.addParam('sysparm_TreqId', g_form.getValue("requested_for"));
    ga.getXML(parseLanguageData);

    var dToday = new Date();
    g_form.setValue('end_date', dToday);
}

function parseLanguageData(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    try {
        if (answer) {
            var ga = new GlideAjax('HVSExpenseUtils');
            ga.addParam('sysparm_name', 'getTrainingCategory');
            ga.addParam('sysparm_Language', answer);
            ga.getXML(parseChoiceData);
        }
    } catch (err) {
        jslog(err.message);
    }
}

function parseChoiceData(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	answer =  JSON.parse(answer);
	g_form.clearOptions("expense_line");
	for(var j=0;j < answer.length;j++) {
		g_form.addOption('expense_line',answer[j].label,answer[j].label,answer[j].value);
	}
}
