catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var ga2 = new GlideAjax('HVSExpenseUtils');
    ga2.addParam('sysparm_name', 'getTrainingData');
    if (newValue != '') ga2.addParam('sysparm_TreqId', newValue);
    else if (newValue == '' && oldValue != '') ga2.addParam('sysparm_TreqId', oldValue);
    ga2.getXML(parseTrainingData);

}

function parseContractInfos(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    answer = JSON.parse(answer);

    try {
        if (answer) {
            if (answer.nb < 2) {
                g_form.setValue("contract", answer.id);
                g_form.setReadOnly("contract", true);
                getMessage('expense.label.Contract', function(msg) {
                    g_form.setLabelOf("contract", msg);
                });
            } else {
                getMessage('expense.contract.field.label', function(msg) {
                    g_form.setLabelOf("contract", msg);
                });
            }
        }
    } catch (err) {
        jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS RFF Set Contract if new requestor - Form Demande de Remboursement Formation]');
    }
}

function parseTrainingData(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    answer = JSON.parse(answer);

    try {
        if (answer) {
            g_form.setValue("training_fee_granted", parseFloat(answer.training_fee_granted).toFixed(2));
            g_form.setValue("accomodation_costs_granted", parseFloat(answer.accomodation_costs_granted).toFixed(2));
            g_form.setValue("travel_expenses_granted", parseFloat(answer.travel_expenses_granted).toFixed(2));
            g_form.setValue("other_expenses_granted", parseFloat(answer.other_expenses_granted).toFixed(2));
            g_form.setValue("training_manager_approver", answer.approver_training);
            g_form.setValue("training_description", answer.training_description);
            g_form.setValue("total_granted", parseFloat(answer.total_granted).toFixed(2));

            g_form.setValue("training_fee_refunded", parseFloat(answer.training_fee_refunded).toFixed(2));
            g_form.setValue("accomodation_costs_refunded", parseFloat(answer.accomodation_costs_refunded).toFixed(2));
            g_form.setValue("travel_expenses_refunded", parseFloat(answer.travel_expenses_refunded).toFixed(2));
            g_form.setValue("other_expenses_refunded", parseFloat(answer.other_expenses_refunded).toFixed(2));
            g_form.setValue("total_refunded", parseFloat(answer.total_refunded).toFixed(2));

            var ga = new GlideAjax('HVSExpenseUtils');
            ga.addParam('sysparm_name', 'getNbContract');
            ga.addParam('mgr_sys_id', g_form.getValue("training_manager_approver"));
            ga.addParam('user_sys_id', g_form.getValue("requested_for"));
            ga.getXML(parseContractInfos);

        }
    } catch (err) {
        jslog(err.message);
    }
}
