catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (newValue == '') {
        return;
    }

    var ga = new GlideAjax('HVSExpenseUtils');
    ga.addParam('sysparm_name', 'getAllowedAmountLeftbyCategory');
    ga.addParam('sysparm_TreqId', this.my_g_form.getValue("training_request"));
    ga.addParam('sysparm_trainingCategory', newValue);
    ga.getXML(displayAmountLeft);

}

function displayAmountLeft(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");

    if (answer) {

        getMessage("Amount left: ", function(msg) {
            g_form.showFieldMsg("expense_line", msg + answer + " CHF");
        });

        g_form.setValue("remaining_refund", answer);
    }
}
