catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    try {
        setInterval(function() {
			//g_form.addInfoMessage( g_form.getValue('mvar_hvs_rff_expense_line_input'));
            var bMvarsEmpyt = (('' || g_form.getValue('mvar_hvs_rff_expense_line_input')) == '');
            if (!bMvarsEmpyt) {
                var multiRowVariableSet = JSON.parse(g_form.getValue('mvar_hvs_rff_expense_line_input'));

                var bTravelByFly = false;
                var sTravelMode = '';

                for (var i = 0; i < multiRowVariableSet.length; i++) {
                    sTravelMode = multiRowVariableSet[i].travel_mode; //get travel mode
                    if (sTravelMode.toLowerCase().indexOf('w_avion') >= 0) {
                        bTravelByFly = true;
                    }
                }

                if (this.document.getElementsByClassName('get-attachment').length == 0 && bTravelByFly) {
                    getMessage('hvs.expense_training.attachment.required', function(msg) {
                        g_form.showFieldMsg("mvar_hvs_rff_expense_line_input", msg);
                    });
                } else {
                    g_form.hideFieldMsg("mvar_hvs_rff_expense_line_input", true);
                }
            } else {
                g_form.hideFieldMsg("mvar_hvs_rff_expense_line_input", true);
            }

        }, 2000);
    } catch (e) {
        //
    }
}
