catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	var sLogaRubric = g_form.getDisplayValue('sr_rubrique_loga');

	if (isLoading || newValue == '') {
		if(g_form.getValue("sr_loga_code") == '806' || g_form.getValue("sr_loga_code") == '79C' || g_form.getValue("sr_loga_code") == '79H') {
			g_form.setMandatory('sr_location_other',false);
			g_form.setMandatory('sr_location_from',true);
			g_form.setMandatory('sr_location_to',true);
			g_form.setDisplay('sr_location_from',true);
			g_form.setDisplay('sr_location_to',true);
			g_form.setReadOnly('sr_location_to',false);
		}
		else {
			g_form.setValue('sr_location','');
			g_form.setMandatory('sr_location',true);
			g_form.setDisplay('sr_location',true);
		}
		return;
	}
	if(g_form.getValue("sr_loga_code") == '806' || g_form.getValue("sr_loga_code") == '79C' || g_form.getValue("sr_loga_code") == '79H') {
		g_form.setValue('sr_location_to','');
		g_form.setMandatory('sr_location_from',false);
		g_form.setMandatory('sr_location_to',false);
		g_form.setDisplay('sr_location_from',false);
		g_form.setDisplay('sr_location_to',false);
	}
	else {
		g_form.setValue('sr_location','');
		g_form.setMandatory('sr_location',false);
		g_form.setDisplay('sr_location',false);
	}
}
