catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
		g_form.hideFieldMsg("amount_to_refund",true);
        return;
    }

	//Get Selected category value : fee, travel, accomodation or other
	var sSelectedCategory = g_form.getValue('expense_line');

	// Get amount input
	var sInputAmount = '' + newValue;
	// Replace any ',' by a '.'
    if (sInputAmount.indexOf(',') > 1) sInputAmount = sInputAmount.replace(',', '.');
	// get numeric value of amount
    var iInputAmount = parseFloat(sInputAmount);

	// IF (Selected category is not fee and amount > 10000) or (incorrect amount value) THEN prevent save and display warning message
    try {
    var $ = this.jQuery;

        if ((iInputAmount <= 0 || iInputAmount >= 10000 || isNaN(iInputAmount)) && sSelectedCategory!='training_fee') {
            getMessage("Invalid Value was entered : '{0}'", function(info) {
                getMessage('expense.rubric_meal.amount.control', function(msg) {
                    g_form.showFieldMsg("amount_to_refund", info.withValues([g_form.getValue("amount_to_refund")]) + ". " + msg);
                });
            });
			$("button[ng-click*='c.saveRow']").addClass( "disabled" );
            return;
        }
		else {
			$("button[ng-click*='c.saveRow']").removeClass( "disabled" );
		}
    } catch (e) {
        jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS RFF Check Amount Refund - Form Demande de Remboursement Formation]');
    }

    var ga = new GlideAjax('HVSExpenseUtils');
    ga.addParam('sysparm_name', 'validateRequestedAmountperCategory');
    ga.addParam('sysparm_TreqId', this.my_g_form.getValue("training_request"));
    ga.addParam('sysparm_trainingCategory', g_form.getValue("expense_line"));
    ga.addParam('sysparm_requestedAmount', g_form.getValue("amount_to_refund"));
    ga.getXML(validateAmount);

    g_form.setValue('amount_to_refund', parseFloat(sInputAmount));
}

function validateAmount(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    if (answer) {

        if (answer == "false") {
            g_form.setValue('amount_to_refund', '');
            getMessage("The amount requested exceeds the amount allocated for the training.", function(msg) {
                g_form.showFieldMsg("amount_to_refund", msg, 'error');
            });
        }
    }
}
