catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }


    var dDate = g_form.getValue("sr_date_expense");

	var ga2 = new GlideAjax('HVSExpenseDateUtils');
    ga2.addParam('sysparm_name', 'ValidateExpenseLineDate');
    ga2.addParam('sysparm_date', dDate);
    ga2.getXML(CheckExpenseDates);

	var ga3 = new GlideAjax('HVSExpenseDateUtils');
	ga3.addParam('sysparm_name', 'ValidateDates');
	ga3.addParam('sysparm_start', dDate);
	ga3.addParam('sysparm_end', '01.08.2020');
	ga3.getXML(CheckMealAmountDisplay);

}

function CheckExpenseDates(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	var oldDate = g_form.getValue("sr_date_expense");

    try {
        if (answer == 'false') {
            getMessage('x_hdvi2_expense.lineDate.not.valid', function(msg) {
                g_form.showFieldMsg('sr_date_expense', msg.replace('{0}', oldDate), 'info');
            });
            g_form.setValue('formok', 'date_invalid');

		} else g_form.setValue('formok', 'true');
    } catch (err) {
        jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS Check Dates - Form Demande de Remboursement]');
    }
}

function CheckMealAmountDisplay(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");

    try {
        if (answer == 'false') {
			g_form.hideFieldMsg("sr_rubrique_loga");
			if(g_form.getValue("sr_rubrique_loga") == 'fbc37b13dbee0090f5e2174b06961949') {
				// Repas dans le Canton
				getMessage('expense.info.frais.repas.dans.canton.new', function(msg) {
					g_form.showFieldMsg("sr_rubrique_loga",msg,"info");
				});
			}
			else if(g_form.getValue("sr_rubrique_loga") == 'bfc37b13dbee0090f5e2174b06961949') {
				// Repas hors Canton
				getMessage('expense.info.frais.repas.hors.canton.new', function(msg) {
					g_form.showFieldMsg("sr_rubrique_loga",msg,"info");
				});
			}
		}
		else {
			g_form.hideFieldMsg("sr_rubrique_loga");
			if(g_form.getValue("sr_rubrique_loga") == 'fbc37b13dbee0090f5e2174b06961949') {
				// Repas dans le Canton
				getMessage('expense.info.frais.repas.dans.canton', function(msg) {
					g_form.showFieldMsg("sr_rubrique_loga",msg,"info");
				});
			}
			else if(g_form.getValue("sr_rubrique_loga") == 'bfc37b13dbee0090f5e2174b06961949') {
				// Repas hors Canton
				getMessage('expense.info.frais.repas.hors.canton', function(msg) {
					g_form.showFieldMsg("sr_rubrique_loga",msg,"info");
				});
			}
		}
		var sValReqAmount = g_form.getValue("sr_requested_amount");
		var sCopyReqAmount = g_form.getValue("amount");

		if(parseFloat(sValReqAmount) <= parseFloat('0'+sCopyReqAmount)) {
			g_form.setValue("sr_requested_amount","");
			g_form.setValue("sr_requested_amount",sValReqAmount);
		} else {
			g_form.setValue("sr_requested_amount","");
			g_form.setValue("sr_requested_amount",sCopyReqAmount);
		}

	}
	catch (err) {
        jslog('A JavaScript runtime error occurred: CheckMealAmountDisplay ' + err.message * '[Client Script HVS Check Date Expense - Form Demande de Remboursement]');
    }
}
