catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	g_form.clearValue("contract", false);

	//Type appropriate comment here, and begin script below

	var ga = new GlideAjax('HVSExpenseUtils');
	ga.addParam('sysparm_name', 'getNbContract');
	ga.addParam('user_sys_id',newValue);
	ga.getXML(parseContractInfos);

}

function parseContractInfos(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	answer = JSON.parse(answer);

	try {
		if(answer) {
			if (answer.nb < 2){
				g_form.setValue("contract", answer.id);
				g_form.setReadOnly("contract", true);
			}
			else
				{
				g_form.setReadOnly("contract", false);
				g_form.setMandatory("contract", true);
			}
		}
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: ' + err.message * '[var HVS Check Contract Length - Form Demande de Remboursement]');
	}


}
