catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') { 
		var ga = new GlideAjax('HVSExpenseGetContractLocation');
		ga.addParam('sysparm_name', 'GetContractLocation');
		ga.addParam('sysparm_contract',oldValue);
		ga.getXML(parseContractLocation);		
		return;
	}

	var ga2 = new GlideAjax('HVSExpenseGetContractLocation');
	ga2.addParam('sysparm_name', 'GetContractLocation');
	if (newValue != '') ga2.addParam('sysparm_contract',newValue);
	else if (newValue == '' && oldValue != '') ga2.addParam('sysparm_contract',oldValue);
	ga2.getXML(parseContractLocation);
}

function parseContractLocation(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	try {
		if(answer) {
			g_form.setValue('locationid',answer);
			g_form.setValue('sr_location_from',answer);
		}
	}
	catch(err){
		jslog(err.message);
	}
}
