catalog_script_client.script:
function onLoad() {
	var $ = this.jQuery,
		fieldName = "training_fee_granted"; // !!! it's the name of any variable from variable set !!!

	setTimeout(function () {
		var $fieldset = $("#sp_formfield_" + fieldName).closest("fieldset"),
			$row = $fieldset.children("div.row"),
			$button = $("<button style='margin-right:10px;margin-bottom:2px;' class='btn btn-icon'>" +
						"<i class='fa fa-minus fa-lg'></i></button>");
		$("div[ng-class='::main.getContainerClasses(container)']").css( "width", "1355px" );
		$fieldset.find("legend") // find(">div>legend")
			.prepend($button);

		$button.click(function () {
			$row.toggle();
			$button.find("i")
				.toggleClass("fa-minus fa-plus");
		});
	}, 0);
}
