sp_widget.client_script:
function($uibModal, $scope) {
	/* widget controller */
	var c = this;
	//Remove " ||data.rolePilot == false" after pilot stage
	if(c.data.manager =="" || c.data.isManagerActived == false || c.data.contract == false || c.data.rolePilot == false){
		c.modalInstance = $uibModal.open({
			templateUrl: 'modalTemplate',
			backdrop:"static",
			scope: $scope
		});
	}



	c.closeModal = function() {
		document.location = "/sphvs";

	};
	c.sendEmail = function(){
		document.location = "mailto:"+c.data.email;
	};

	c.intranet = function(){
		document.location = "https://sirhportal.hopitalvs.ch";
	};


}

sp_widget.script:
(function() {

	var manager = "";
	var active = true;
	var email_if_loga_data_wrong = gs.getProperty("x_hdvi2_expense.email_contact");

	var user = new GlideRecord('sys_user');
	if(user.get(gs.getUserID())){
		manager = ""+user.manager.name;
		if(user.manager.active == false){
			active = false;
		}
		if(user.company.u_email_hr){
			email_if_loga_data_wrong = ""+user.company.u_email_hr;
		}

	}

	var grUserContract = new GlideRecord('u_hvs_user_contract');
	var cptContract = 0;
	grUserContract.addQuery('u_user',gs.getUserID());
	grUserContract.addQuery('u_active',true);
	grUserContract.query();

	while (grUserContract.next()) {
		cptContract = cptContract + 1;
	}
	if(cptContract==0) data.contract=false;
	else data.contract=true;

	// TO REMOVE after pilot stage
	data.rolePilot = true ; //gs.hasRole("hvs_pilot_expense");

	//----------------------------
	data.manager = manager;
	data.email = email_if_loga_data_wrong;
	data.isManagerActived = active;


})();
