sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if(g_form.getValue("refunded_amount") != g_form.getValue("requested_amount") && g_form.getValue("refunded_value") == g_form.getValue("requested_value")){
		g_form.setVisible("update_reason",true);
		g_form.setMandatory("update_reason",true);
	}else if(g_form.getValue("refunded_amount") == g_form.getValue("requested_amount") && g_form.getValue("refunded_value") == g_form.getValue("requested_value")){
	g_form.setMandatory("update_reason",false);
	g_form.setVisible("update_reason",false);
}


   //Type appropriate comment here, and begin script below

}
