sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	// Add your code here
	var today_date = new GlideDate();
	var next_date = new GlideDate();
	var next_month = next_date.addMonthsUTC(1);

	var monthNames_de = 'Januar,Februar,März,April,Mai,Juni,Juli,August,September,Oktober,November,Dezember'.split(',');

	var strMsg = "";

	strMsg = 'Guten Tag,<p><br>Es liegen Anträge zur Kostenrückerstattungen für Sie zur Validierung bereit. <a href="/exphvs?id=exp_my_team">Klicken Sie hier</a>.<br>Diese müssen vor dem 20.  ' ;

	var month_target ='';
	if(today_date.getDayOfMonthUTC() < 20) 
			month_target = monthNames_de[today_date.getMonthUTC()-1] ;
	else
			month_target = monthNames_de[next_date.getMonthUTC()-1] ;
	strMsg = strMsg + month_target;

	strMsg = strMsg +" validiert werden." ;
	strMsg = strMsg + '<p><br>Besten Dank.' ;

	template.print(strMsg);

})(current, template, email, email_action, event);
