sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	// Add your code here
	var today_date = new GlideDate();
	var next_date = new GlideDate();
	var next_month = next_date.addMonthsUTC(1);

	var monthNames_fr = 'Janvier,Février,Mars,Avril,Mai,Juin,Juillet,Août,Septembre,Ocotbre,Novembre,Décembre'.split(',');

	var strMsg = "";

	strMsg = 'Bonjour,<p><br>Vous avez des demandes de remboursement à valider. <a href="/exphvs?id=exp_my_team">Cliquez ici</a><br>Ces demandes doivent être validées avant le 20 ' ;

	var month_target ='';
	if(today_date.getDayOfMonthUTC() < 20) 
		month_target = monthNames_fr[today_date.getMonthUTC()-1] ;
	else 
		month_target = monthNames_fr[next_date.getMonthUTC()-1] ;

	strMsg = strMsg + month_target;
	strMsg = strMsg + '<p><br>Merci.' ;

	template.print(strMsg);

})(current, template, email, email_action, event);
