sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {

	var approval = new GlideRecord('sysapproval_approver');
	approval.addNotNullQuery('approver');
	approval.addQuery('document_id', current.sys_id.toString());
	approval.addQuery('state', 'rejected');
	approval.orderByDesc('sys_created_on');
	approval.setLimit(1);
	approval.query();
	if(approval.next()){
		template.print('<br/>' + gs.getMessage('hvs.expense.notification.refus') + approval.approver.getDisplayValue() + '.');
		template.print('<br/><br/>' + gs.getMessage('hvs.expense.notification.commentaire') + ': <b>' + approval.comments + '</b>');
	}

})(current, template, email, email_action, event);
