sys_script_include.script:
var HVSExpenseGetContractLocation = Class.create();
HVSExpenseGetContractLocation.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

GetContractLocation: function () {

		var ret_1 ='';
		var locid=this.getParameter("sysparm_contract");
		var grUserContract = new GlideRecord('u_hvs_user_contract');
		grUserContract.addQuery('sys_id',locid);
		grUserContract.addQuery('u_active',true);
		grUserContract.query();

		while (grUserContract.next()) {
			ret_1 = grUserContract.u_user_job_location;
		}

		var JobLocation ='';
		var grExpenseLocation = new GlideRecord('x_hdvi2_expense_expense_travel_km');
		grExpenseLocation.addEncodedQuery('locations='+ret_1+'^km=0^ORkm=NULL');
		grExpenseLocation.query();

		while (grExpenseLocation.next()) {
			JobLocation = grExpenseLocation.sys_id;
		}

		return JobLocation;
	},

	type: 'HVSExpenseGetContractLocation'
});
