sys_script_include.script:
var HVSExpenseDateUtils = Class.create();
HVSExpenseDateUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

		/* 
	* DÃ©termine la date de livraison selon le cat item
	*/
	GetDate: function() {
		var nowDT = GlideDateTime();

		return nowDT.getDate().getDisplayValue();
	},	

	ValidateDates: function ()
	{
		var startDT = new GlideDate();
		startDT.setDisplayValue(this.getParameter('sysparm_start'));
		var endDT = new GlideDate();
		endDT.setDisplayValue(this.getParameter('sysparm_end'));

		if (startDT > endDT){
			return false;
		}else{
			return true;
		}
	},

	ValidateExpenseLineDate: function ()
	{
		var expenseDT = new GlideDate();
		expenseDT.setDisplayValue(this.getParameter('sysparm_date'));
		var nowDT = new GlideDate();

		if (expenseDT > nowDT){
			return false;
		}else{
			return true;
		}
	},

	type: 'HVSExpenseDateUtils'
});
