sys_ui_page.client_script:
function actionOK(){
    var reject = gel('rejection_reason').value;
    if (reject =="" || reject ==" ") {
        alert(getMessage("Please select a rejection reason"));
        return false;
	}
	var c = gel('cancel_or_submit');
    c.value = "submit";
	return true;
}

function cancel(){
	var c = gel('cancel_or_submit');
    c.value = "cancel";
	return true;
}

sys_ui_page.processing_script:
function submitRejection(){

	if (cancel_or_submit == "submit") {
		var gr_exp = new GlideRecord('x_hdvi2_expense_sheet');
		if(gr_exp.get(expense_id)){
			gr_exp.status = -1;
			gr_exp.reviewed_on = new GlideDateTime().getDisplayValue();
			gr_exp.comments = rejection_reason;
			gr_exp.update();
			gs.addInfoMessage(gs.getMessage("The expense has been rejected and is now canceled."));

		}

	} 
response.sendRedirect("x_hdvi2_expense_sheet.do?sys_id=" + expense_id);

}
submitRejection();
