sys_ui_page.client_script:
function actionOK() {
	// PSO 28.10.20 Get Extraction type
    var iExtractType  = parseInt(gel('extraction_type').value);
    // PSO end of modification

	var dateTmp = gel('date').value;
    if (dateTmp == "") {
        alert(getMessage("Please select an attribution date"));
        return false;
    }

    // PSO 09.04.2020 Formet date value to YYYY-MM-dd
    var year = dateTmp.substr(6, 4);
    var month = dateTmp.substr(3, 2);
    var ad = gel('attr_date');
    ad.value = year + "-" + month + "-01";
    var attr_date = ad.value;
   // PSO end of modification

    var c = gel('cancel_or_submit');
    c.value = "submit";
    return true;
}

function cancel() {
    var c = gel('cancel_or_submit');
    c.value = "cancel";
    return true;
}

function validateDate(date) {
    var newTime = new GwtDate(date + '');
    if (newTime.compare(timeWindow(), true) < 0) {
        setValidationError(true, new GwtMessage().getMessage('Lease end time must be later than current time'));
        return false;
    }
}

sys_ui_page.processing_script:
var sRedirectionRecord = "";
var sRedirectionList = "";

if (extraction_type == "1") {
    sRedirectionList = "x_hdvi2_btask_salary_advance_extract_list.do";
    sRedirectionRecord = "x_hdvi2_btask_salary_advance_extract.do";
}
else {
    sRedirectionList = "x_hdvi2_expense_extract_list.do";
    sRedirectionRecord = "x_hdvi2_expense_extract.do";
}
if (cancel_or_submit == "submit") {

	var iExtractType = parseInt(extraction_type);

	try {
    var generation = new x_hdvi2_expense.HVSExpenseExtractUtils().generateExtraction(attr_date, iExtractType);
    if (generation == "") {
        var msg = gs.getMessage("Error to create an extract record, please contact the ServiceNow admin");
        gs.addErrorMessage(msg);
        response.sendRedirect(sRedirectionList);
    } else {
        response.sendRedirect(sRedirectionRecord + "?sys_id=" + generation);
    }
	}
	catch(err) {
		gs.addErrorMessage(err);
	}

} else if (cancel_or_submit == "cancel") {
    response.sendRedirect(sRedirectionList);
}
