sys_ui_page.client_script:
function actionOK(){
	var c = gel('cancel_or_submit');
    c.value = "conitnue";
	return true;
}

function cancel(){
	var c = gel('cancel_or_submit');
    c.value = "cancel";
	return true;
}

sys_ui_page.processing_script:
function ProcessAction() {

    if (cancel_or_submit == "conitnue") {
        var gr_ExpSheet = new GlideRecord('x_hdvi2_expense_sheet');
        var gr_ExpLine = new GlideRecord('x_hdvi2_expense_line');
        gr_ExpLine.addEncodedQuery('active=true^rubric=e8e4b084db44149009b962a916961906^ORrubric=9805f444db44149009b962a916961908^ORrubric=edc49704dbd3c050f5e2174b06961901^ORrubric=fbc37b13dbee0090f5e2174b06961948^status=2');
        gr_ExpLine.query();
        while (gr_ExpLine.next()) {
            if (gr_ExpSheet.get(gr_ExpLine.expense)) {
                gr_ExpSheet.status = 3;
                gr_ExpSheet.update();
            }
            gr_ExpLine.status = 3;
            gr_ExpLine.update();
        }

        gs.addInfoMessage(gs.getMessage("expense.KM.mass.approval.done"));
    }
    response.sendRedirect("x_hdvi2_expense_sheet_list.do?sysparm_query=active%3Dtrue%5EstatusIN2%2C3%5EEQ");
}
ProcessAction();
