sys_ui_page.client_script:
function actionOK() {
    var c = gel('cancel_or_submit');
    c.value = "continue";
    var new_category = gel('miscellaneous_account_code').value;
	var id_exp = gel('id_expense_line').value;

    if (new_category == '' || new_category == null) {

        var msg = getMessage("You need to select a new category");
        alert(msg);

        return false;

    } else {
        return true;
    }

}

function cancel() {
    var c = gel('cancel_or_submit');
    c.value = "cancel";
    GlideDialogWindow.get().destroy();
    return false;
}

sys_ui_page.processing_script:
if (cancel_or_submit == "continue") {

    var gr_expense_line = new GlideRecord("x_hdvi2_expense_line");
    if (gr_expense_line.get(id_expense_line)) {
        if (gr_expense_line.status == 2) {
            var gr_choice = new GlideRecord("question_choice");
            if (gr_choice.get(miscellaneous_account_code)) {
                gr_expense_line.miscellaneous_account_code = gr_choice.text;
                gr_expense_line.update();
                gs.addInfoMessage(gs.getMessage("The expense line has been successfully updated"));
            } else {
                gs.addErrorMessage(gs.getMessage("The category has not been found"));
            }



        } else {
            gs.addErrorMessage(gs.getMessage("The expense line can NOT be updated in the current state"));
        }

    }
    response.sendRedirect("x_hdvi2_expense_line.do?sys_id=" + id_expense_line);

}
