/*-------------------
The manager will be defined with the following logic:
1) Take the manager who has approved the training request, if still active
2) If the contract manager is active, we will take the user contract manager
3) If the user (sys_user) manager is active, we will take the user manager

At the end, we will be sure the manager get the "Expense Approver" tag

-------------------*/
var manager = "";


var gr_training = new GlideRecord("x_hdvi2_training_m_request");
if (gr_training.get(current.source)) {
    if (gr_training.manager.active == true) {
        manager = gr_training.manager;
    }
}

if (manager == "") {

    if (current.contract.u_user_managers.active == true) {
        manager = current.contract.u_user_managers;
    } else if (current.requested_for.manager.active == true) {
        manager = current.requested_for.manager;
    }
}

if (manager) {
    var gr_manager = new GlideRecord("sys_user");
    if (gr_manager.get(manager)) {
        if (gr_manager.x_hdvi2_expense_approver == false) {
            gr_manager.x_hdvi2_expense_approver = true;
            gr_manager.update();
        }
    }
}


//We will now validate if there is any approval delegation

var gdt = new GlideDateTime();
var gr_delegate = new GlideRecord('sys_user_delegate');
gr_delegate.addQuery("user", manager);
gr_delegate.addQuery("u_expense_approval", true);
gr_delegate.addQuery("starts", "<=", gdt.getDisplayValue());
gr_delegate.addQuery("ends", ">=", gdt.getDisplayValue());
gr_delegate.query();
while (gr_delegate.next()) {
    manager += "," + gr_delegate.delegate.sys_id;

    var gr_manager = new GlideRecord("sys_user");
    if (gr_manager.get(gr_delegate.delegate.sys_id)) {
        if (gr_manager.u_expense_approver == false) {
            gr_manager.u_expense_approver = true;
            gr_manager.update();
        }

    }
}



current.approver = manager;
current.approval = "requested";

