var gr_training = new GlideRecord(current.source_table);
if (gr_training.get(current.source)) {
    if (current.last_expense_for_related_source == true) {
        gr_training.refund_state = "refunded";
    } else {
        gr_training.refund_state = "refund_in_progress";
    }

    gr_training.update();
}

